import React, { useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { loadStripe } from "@stripe/stripe-js";
import "./Checkout.css";

const stripePromise = loadStripe("pk_test_51OmV8ISCq30aaaQjE3SoQsQ5heolFCZaWYYNDJcbPxsFpGIBPbGfaiYKdJiFeRpO2762fOrpZ0lBVJXN3koCda21001EN999ds");

function Checkout() {
  const navigate = useNavigate();
  const location = useLocation();
  const totalAmount = location.state?.totalAmount || 0;

  const [form, setForm] = useState({
    name: "",
    email: "",
    address: "",
    phone: "",
  });

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  // ======================= STRIPE PAYMENT =======================
  const handleStripePayment = async () => {
  try {
    const response = await fetch("http://localhost:5000/create-checkout-session", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ amount: totalAmount }),
    });

    const session = await response.json();

    // NEW redirect method (required for new Stripe versions)
    window.location.href = session.url;

  } catch (error) {
    console.log("Payment error:", error);
  }
};
  // ======================= PLACE ORDER WITHOUT PAYMENT =======================
  const handleSubmit = async (e) => {
    e.preventDefault();

    await fetch("http://localhost:5000/order", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ ...form, totalAmount }),
    });

    alert("Order placed successfully!");

    await fetch(`http://localhost:5000/cart/clear/1`, { method: "DELETE" });

    navigate("/thank-you", { state: { totalAmount } });
  };

  return (
    <div className="checkout-container">
      <h2>Checkout</h2>
      <h3 className="total-box">Total Payable: ₹{totalAmount}</h3>

      <form onSubmit={handleSubmit} className="checkout-form">
        <input name="name" placeholder="Full Name" value={form.name} onChange={handleChange} required />
        <input name="email" type="email" placeholder="Email" value={form.email} onChange={handleChange} required />
        <input name="phone" placeholder="Phone Number" value={form.phone} onChange={handleChange} required />
        <textarea name="address" placeholder="Address" value={form.address} onChange={handleChange} required />

        <button type="submit" className="place-order-btn">Place Order (Without Payment)</button>

        <button type="button"
          className="place-order-btn"
          style={{ marginTop: "15px", background: "black", color: "white" }}
          onClick={handleStripePayment}>
          Pay Now with Stripe
        </button>
      </form>
    </div>
  );
}

export default Checkout;
