import React, { useEffect, useState } from "react";
import { useSearchParams } from "react-router-dom";
import "./ThankYou.css";

function ThankYou() {
  const [params] = useSearchParams();
  const sessionId = params.get("session_id");
  const [data, setData] = useState(null);

  useEffect(() => {
    fetch("http://localhost:5000/save-transaction", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ session_id: sessionId, user_id: 1 })
    })
    .then(() =>
      fetch(`http://localhost:5000/transaction/${sessionId}`)
    )
    .then(res => res.json())
    .then(setData);
  }, [sessionId]);

  if (!data) return <h3>Loading payment details...</h3>;

  return (
    <div className="thankyou-box">
      <h1>ðŸŽ‰ Thank You!</h1>

      <p><b>Transaction ID:</b> {data.transaction_id}</p>
      <p><b>Status:</b> {data.status}</p>
      <p><b>Total Paid:</b> â‚¹{data.amount}</p>
      <p><b>Email:</b> {data.email}</p>

      <button onClick={() => window.location.href="/"}>
        Continue Shopping
      </button>
    </div>
  );
}

export default ThankYou;
