import React, { useEffect, useState } from "react";
import "./CartPage.css";

function CartPage() {
  const [items, setItems] = useState([]);
  const userId = 1; // static user id

  // Fetch cart items
  const loadCart = async () => {
    const res = await fetch(`http://localhost:5000/cart/${userId}`);
    const data = await res.json();
    setItems(data);
  };

  useEffect(() => {
    loadCart();
  }, []);

  // Update quantity in frontend + backend
  const updateQty = async (cart_id, currentQty, type) => {
    let newQty = type === "inc" ? currentQty + 1 : currentQty - 1;

    if (newQty < 1 || newQty > 5) return;

    await fetch("http://localhost:5000/cart/update", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ cart_id, qty: newQty }),
    });

    loadCart();
  };

  // Remove item
  const removeItem = async (cart_id) => {
    await fetch(`http://localhost:5000/cart/remove/${cart_id}`, {
      method: "DELETE",
    });
    loadCart();
  };

  // Calculate total amount
  const totalAmount = items.reduce(
    (sum, item) => sum + item.price * item.qty,
    0
  );

  return (
    <div className="cart-container">
      <h2>Your Cart</h2>

      <table className="cart-table">
        <thead>
          <tr>
            <th>Image</th>
            <th>Product</th>
            <th>Qty</th>
            <th>Price</th>
            <th>Total</th>
            <th>Remove</th>
          </tr>
        </thead>

        <tbody>
          {items.map((item) => (
            <tr key={item.cart_id}>
              <td>
                <img
                  src={`http://localhost:5000/uploads/${item.image}`}
                  alt=""
                  className="cart-img"
                />
              </td>

              <td>{item.title}</td>

              <td>
                <button
                  className="qty-btn"
                  onClick={() =>
                    updateQty(item.cart_id, item.qty, "dec")
                  }
                  disabled={item.qty <= 1}
                >
                  -
                </button>

                <span className="qty">{item.qty}</span>

                <button
                  className="qty-btn"
                  onClick={() =>
                    updateQty(item.cart_id, item.qty, "inc")
                  }
                  disabled={item.qty >= 5}
                >
                  +
                </button>
              </td>

              <td>₹{item.price}</td>
              <td>₹{item.price * item.qty}</td>

              <td>
                <button
                  className="remove-btn"
                  onClick={() => removeItem(item.cart_id)}
                >
                  Remove
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <h3 className="total-box">Grand Total: ₹{totalAmount}</h3>
    </div>
  );
}

export default CartPage;
