import React, { useState } from "react";
import { Link, useLocation } from "react-router-dom";

const Sidebar = () => {
  const location = useLocation();
  const [openMenus, setOpenMenus] = useState({});

  // Toggle submenu
  const toggleMenu = (menuId) => {
    setOpenMenus((prev) => ({
      ...prev,
      [menuId]: !prev[menuId]
    }));
  };

  // Check if path is active
  const isActive = (path) => {
    return location.pathname === path;
  };

  // Check if menu contains active link
  const hasActiveChild = (paths) => {
    return paths.some((path) => location.pathname === path);
  };

  // Menu items configuration with absolute paths
  const menuItems = [
    {
      id: "dashboard",
      title: "Dashboard",
      icon: "mdi-home",
      path: "/admin",
      type: "link"
    },
    {
      id: "ui-basic",
      title: "Basic UI Elements",
      icon: "mdi-crosshairs-gps",
      type: "collapse",
      hasArrow: true,
      children: [
        { title: "Buttons", path: "/admin/buttons" },
        { title: "Dropdowns", path: "/admin/dropdowns" },
        { title: "Typography", path: "/admin/typography" }
      ]
    },
    {
      id: "icons",
      title: "Icons",
      icon: "mdi-contacts",
      type: "collapse",
      children: [
        { title: "Font Awesome", path: "/admin/icons" }
      ]
    },
    {
      id: "forms",
      title: "Forms",
      icon: "mdi-format-list-bulleted",
      type: "collapse",
      children: [
        { title: "Form Elements", path: "/admin/forms" }
      ]
    },
    {
      id: "charts",
      title: "Charts",
      icon: "mdi-chart-bar",
      type: "collapse",
      children: [
        { title: "ChartJs", path: "/admin/charts" }
      ]
    },
    {
      id: "tables",
      title: "Tables",
      icon: "mdi-table-large",
      type: "collapse",
      children: [
        { title: "Basic Table", path: "/admin/tables" }
      ]
    },
    {
      id: "auth",
      title: "User Pages",
      icon: "mdi-lock",
      type: "collapse",
      hasArrow: true,
      children: [
        { title: "Login", path: "/admin/login" },
        { title: "Register", path: "/admin/register" }
      ]
    },
    {
      id: "documentation",
      title: "Documentation",
      icon: "mdi-file-document-box",
      path: "#",
      type: "external"
    }
  ];

  return (
    <nav className="sidebar sidebar-offcanvas" id="sidebar">
      <ul className="nav">
        {/* Profile */}
        <li className="nav-item nav-profile">
          <Link to="/admin" className="nav-link">
            <div className="nav-profile-image">
              <img src="/admin-assets/images/faces/face1.jpg" alt="profile" />
              <span className="login-status online"></span>
            </div>
            <div className="nav-profile-text d-flex flex-column">
              <span className="font-weight-bold mb-2">David Grey. H</span>
              <span className="text-secondary text-small">Project Manager</span>
            </div>
            <i className="mdi mdi-bookmark-check text-success nav-profile-badge"></i>
          </Link>
        </li>

        {/* Menu Items */}
        {menuItems.map((item) => {
          if (item.type === "link") {
            return (
              <li className={`nav-item ${isActive(item.path) ? "active" : ""}`} key={item.id}>
                <Link className="nav-link" to={item.path}>
                  <span className="menu-title">{item.title}</span>
                  <i className={`mdi ${item.icon} menu-icon`}></i>
                </Link>
              </li>
            );
          }

          if (item.type === "collapse") {
            const childPaths = item.children.map((child) => child.path);
            const isMenuActive = hasActiveChild(childPaths);
            const isOpen = openMenus[item.id] || isMenuActive;

            return (
              <li className={`nav-item ${isMenuActive ? "active" : ""}`} key={item.id}>
                <button
                  className={`nav-link w-100 text-start ${isOpen ? "" : "collapsed"}`}
                  onClick={() => toggleMenu(item.id)}
                  style={{ background: "none", border: "none" }}
                  type="button"
                  aria-expanded={isOpen}
                >
                  <span className="menu-title">{item.title}</span>
                  {item.hasArrow && <i className="menu-arrow"></i>}
                  <i className={`mdi ${item.icon} menu-icon`}></i>
                </button>

                <div className={`collapse ${isOpen ? "show" : ""}`}>
                  <ul className="nav flex-column sub-menu">
                    {item.children.map((child, index) => (
                      <li className="nav-item" key={index}>
                        <Link
                          className={`nav-link ${isActive(child.path) ? "active" : ""}`}
                          to={child.path}
                        >
                          {child.title}
                        </Link>
                      </li>
                    ))}
                  </ul>
                </div>
              </li>
            );
          }

          if (item.type === "external") {
            return (
              <li className="nav-item" key={item.id}>
                <a className="nav-link" href={item.path} target="_blank" rel="noopener noreferrer">
                  <span className="menu-title">{item.title}</span>
                  <i className={`mdi ${item.icon} menu-icon`}></i>
                </a>
              </li>
            );
          }

          return null;
        })}
      </ul>
    </nav>
  );
};

export default Sidebar;
