import React, { useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import "./ThankYou.css";

function ThankYou() {
  const location = useLocation();
  const navigate = useNavigate();
  const totalAmount = location.state?.totalAmount || 0;

  // ðŸ”¥ clear cart from backend on order success
  useEffect(() => {
    fetch("http://localhost:5000/cart/clear/1", { method: "DELETE" })
      .then(() => console.log("Cart cleared successfully"));
  }, []);

  return (
    <div className="thankyou-wrapper">
      <div className="thankyou-box">
        <h1>ðŸŽ‰ Thank You!</h1>
        <p>Your order has been placed successfully.</p>

        <h3>Total Paid: â‚¹{totalAmount}</h3>

        <button onClick={() => navigate("/cart")} className="back-home-btn">
          View Cart (Should be empty)
        </button>

        <button onClick={() => navigate("/")} className="back-home-btn">
          Continue Shopping
        </button>
      </div>
    </div>
  );
}

export default ThankYou;
