import React, { useEffect, useState } from "react";

function CartPage() {
  const [cart, setCart] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/cart/1")
      .then((res) => res.json())
      .then((data) => setCart(data))
      .catch(console.error);
  }, []);

  const removeItem = async (cart_id) => {
    await fetch(`http://localhost:5000/cart/remove/${cart_id}`, {
      method: "DELETE",
    });

    setCart(cart.filter((item) => item.cart_id !== cart_id));
  };

  const totalPrice = cart.reduce(
    (sum, item) => sum + item.price * item.qty,
    0
  );

  return (
    <div className="cart-container">
      <h1>Your Cart</h1>

      {cart.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <table className="cart-table">
          <thead>
            <tr>
              <th>Product</th>
              <th>Image</th>
              <th>Qty</th>
              <th>Price</th>
              <th>Total</th>
              <th>Remove</th>
            </tr>
          </thead>

          <tbody>
            {cart.map((item) => (
              <tr key={item.cart_id}>
                <td>{item.title}</td>

                <td>
                  <img
                    src={`http://localhost:5000/uploads/${item.image}`}
                    alt={item.title}
                    width="60"
                  />
                </td>

                <td>{item.qty}</td>
                <td>₹{item.price}</td>
                <td>₹{item.price * item.qty}</td>

                <td>
                  <button
                    className="btn-remove"
                    onClick={() => removeItem(item.cart_id)}
                  >
                    Remove
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      <h2 className="cart-total">Grand Total: ₹{totalPrice}</h2>
    </div>
  );
}

export default CartPage;
