import React from "react";
import { Link } from "react-router-dom";

function ProductCard({ product }) {
  // Add to cart function
  const addToCart = () => {
    const user_id = 1; // Demo user ID
    fetch("http://localhost:5000/cart", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ user_id, product_id: product.id, qty: 1 }),
    })
      .then((res) => res.json())
      .then((data) => alert(data.message))
      .catch((err) => console.error(err));
  };

  return (
    <div className="product-card">
      <img
        src={`http://localhost:5000/uploads/${product.image}`}
        alt={product.title}
        className="product-img"
      />
      <h3>{product.title}</h3>
      <p>â‚¹ {parseFloat(product.price).toFixed(2)}</p>

      <div className="product-actions">
        <button className="btn add-cart-btn" onClick={addToCart}>
          Add to Cart
        </button>
        <Link to={`/product/${product.id}`} className="btn details-btn">
          View Details
        </Link>
      </div>
    </div>
  );
}

export default ProductCard;
