import React, { useEffect, useState } from "react";
import "./CartPage.css";
import { useNavigate } from "react-router-dom";

function CartPage() {
  const [items, setItems] = useState([]);
  const userId = 1;
  const navigate = useNavigate();

  const loadCart = async () => {
    const res = await fetch(`http://localhost:5000/cart/${userId}`);
    const data = await res.json();
    setItems(data);
  };

  useEffect(() => { loadCart(); }, []);

  // ðŸ”¥ Auto-refresh cart when user returns from ThankYou page
  useEffect(() => {
    const refresh = () => loadCart();
    window.addEventListener("focus", refresh);
    return () => window.removeEventListener("focus", refresh);
  }, []);

  const updateQty = async (cart_id, qty, type) => {
    const newQty = type === "inc" ? qty+1 : qty-1;
    if(newQty < 1 || newQty>5) return;

    await fetch("http://localhost:5000/cart/update",{
      method:"POST",
      headers:{ "Content-Type":"application/json"},
      body:JSON.stringify({ cart_id, qty:newQty })
    });

    loadCart();
  };

  const removeItem = async(cart_id)=>{
    await fetch(`http://localhost:5000/cart/remove/${cart_id}`,{method:"DELETE"});
    loadCart();
  };

  const totalAmount = items.reduce((s,i)=>s+i.price*i.qty,0);

  return(
    <div className="cart-container">
      <h2>Your Cart</h2>

      {items.length===0?
        <p className="empty-cart">ðŸ›’ Your cart is empty</p>
      :
      <>
       <table className="cart-table">
          <thead>
            <tr><th>Image</th><th>Name</th><th>Qty</th><th>Price</th><th>Total</th><th>Remove</th></tr>
          </thead>
          <tbody>
            {items.map(i=>(
              <tr key={i.cart_id}>
                <td><img src={`http://localhost:5000/uploads/${i.image}`} width="60"/></td>
                <td>{i.title}</td>
                <td>
                  <button onClick={()=>updateQty(i.cart_id,i.qty,"dec")}>-</button>
                  {i.qty}
                  <button onClick={()=>updateQty(i.cart_id,i.qty,"inc")}>+</button>
                </td>
                <td>â‚¹{i.price}</td>
                <td>â‚¹{i.qty*i.price}</td>
                <td><button onClick={()=>removeItem(i.cart_id)}>X</button></td>
              </tr>
            ))}
          </tbody>
       </table>

       <div className="checkout-box">
         <h3>Total: â‚¹{totalAmount}</h3>
         <button onClick={()=>navigate("/checkout",{state:{totalAmount}})} className="checkout-btn">
            Buy Now
         </button>
       </div>
      </>
      }
    </div>
  );
}

export default CartPage;
