import React, { useEffect, useState } from "react";
import ProductCard from "./ProductCard";
import "./ProductCard.css";

const PAGE_SIZE = 8;

function ProductList() {
  const [products, setProducts] = useState([]);
  const [search, setSearch] = useState("");
  const [page, setPage] = useState(1);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setLoading(true);
    fetch("http://localhost:5000/products")
      .then((res) => res.json())
      .then((data) => {
        setProducts(data);
        setLoading(false);
      })
      .catch((err) => {
        console.error(err);
        setLoading(false);
      });
  }, []);

  // Filter products by search text
  const filtered = products.filter((p) =>
    p.title.toLowerCase().includes(search.toLowerCase())
  );

  // Pagination logic
  const totalPages = Math.ceil(filtered.length / PAGE_SIZE);
  const paginated = filtered.slice((page - 1) * PAGE_SIZE, page * PAGE_SIZE);

  // Inline styles for guaranteed layout
  const containerStyle = {
    maxWidth: "1400px",
    margin: "0 auto",
    padding: "40px 20px",
  };

  const headerStyle = {
    textAlign: "center",
    marginBottom: "40px",
  };

  const titleStyle = {
    fontSize: "42px",
    fontWeight: "800",
    color: "#1a1a2e",
    marginBottom: "10px",
    background: "linear-gradient(135deg, #667eea 0%, #764ba2 100%)",
    WebkitBackgroundClip: "text",
    WebkitTextFillColor: "transparent",
    backgroundClip: "text",
  };

  const subtitleStyle = {
    fontSize: "18px",
    color: "#666",
  };

  const searchWrapperStyle = {
    display: "flex",
    justifyContent: "center",
    marginBottom: "40px",
  };

  const searchBoxStyle = {
    position: "relative",
    width: "100%",
    maxWidth: "500px",
  };

  const searchInputStyle = {
    width: "100%",
    padding: "16px 20px 16px 50px",
    fontSize: "16px",
    border: "2px solid #e0e0e0",
    borderRadius: "50px",
    outline: "none",
    background: "#fff",
  };

  const searchIconStyle = {
    position: "absolute",
    left: "18px",
    top: "50%",
    transform: "translateY(-50%)",
    fontSize: "18px",
    color: "#999",
  };

  const gridStyle = {
    display: "grid",
    gridTemplateColumns: "repeat(4, 1fr)",
    gap: "30px",
    marginBottom: "50px",
  };

  const loadingStyle = {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    padding: "80px 20px",
  };

  const paginationStyle = {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    gap: "15px",
    marginTop: "30px",
    flexWrap: "wrap",
  };

  const paginationBtnStyle = {
    padding: "12px 24px",
    fontSize: "14px",
    fontWeight: "600",
    color: "#667eea",
    background: "#fff",
    border: "2px solid #667eea",
    borderRadius: "8px",
    cursor: "pointer",
  };

  const pageNumStyle = (isActive) => ({
    width: "40px",
    height: "40px",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    fontSize: "14px",
    fontWeight: "600",
    color: isActive ? "#fff" : "#333",
    background: isActive ? "linear-gradient(135deg, #667eea 0%, #764ba2 100%)" : "#fff",
    border: isActive ? "none" : "2px solid #e0e0e0",
    borderRadius: "8px",
    cursor: "pointer",
  });

  // Responsive grid columns based on window width
  const getGridColumns = () => {
    if (typeof window !== "undefined") {
      if (window.innerWidth <= 600) return "repeat(1, 1fr)";
      if (window.innerWidth <= 900) return "repeat(2, 1fr)";
      if (window.innerWidth <= 1200) return "repeat(3, 1fr)";
    }
    return "repeat(4, 1fr)";
  };

  const [gridColumns, setGridColumns] = useState(getGridColumns());

  useEffect(() => {
    const handleResize = () => setGridColumns(getGridColumns());
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  return (
    <div style={containerStyle}>
      {/* Header Section */}
      <div style={headerStyle}>
        <h1 style={titleStyle}>Shop Products</h1>
        <p style={subtitleStyle}>Discover our amazing collection</p>
      </div>

      {/* Search Bar */}
      <div style={searchWrapperStyle}>
        <div style={searchBoxStyle}>
          <span style={searchIconStyle}>üîç</span>
          <input
            type="text"
            placeholder="Search for products..."
            value={search}
            onChange={(e) => {
              setSearch(e.target.value);
              setPage(1);
            }}
            style={searchInputStyle}
          />
        </div>
      </div>

      {/* Loading State */}
      {loading ? (
        <div style={loadingStyle}>
          <div
            style={{
              width: "50px",
              height: "50px",
              border: "4px solid #f3f3f3",
              borderTop: "4px solid #667eea",
              borderRadius: "50%",
              animation: "spin 1s linear infinite",
              marginBottom: "20px",
            }}
          ></div>
          <p style={{ color: "#666" }}>Loading products...</p>
        </div>
      ) : (
        <>
          {/* Products Grid */}
          <div style={{ ...gridStyle, gridTemplateColumns: gridColumns }}>
            {paginated.length === 0 ? (
              <div
                style={{
                  gridColumn: "1 / -1",
                  textAlign: "center",
                  padding: "60px 20px",
                  background: "#fff",
                  borderRadius: "16px",
                }}
              >
                <h3 style={{ fontSize: "24px", color: "#1a1a2e", marginBottom: "10px" }}>
                  No products found
                </h3>
                <p style={{ color: "#666" }}>Try adjusting your search criteria</p>
              </div>
            ) : (
              paginated.map((product) => (
                <ProductCard key={product.id} product={product} />
              ))
            )}
          </div>

          {/* Pagination */}
          {totalPages > 1 && (
            <div style={paginationStyle}>
              <button
                onClick={() => setPage((p) => Math.max(p - 1, 1))}
                disabled={page === 1}
                style={{
                  ...paginationBtnStyle,
                  opacity: page === 1 ? 0.5 : 1,
                  cursor: page === 1 ? "not-allowed" : "pointer",
                }}
              >
                ‚Üê Previous
              </button>

              <div style={{ display: "flex", gap: "8px" }}>
                {[...Array(totalPages)].map((_, i) => (
                  <button
                    key={i}
                    onClick={() => setPage(i + 1)}
                    style={pageNumStyle(page === i + 1)}
                  >
                    {i + 1}
                  </button>
                ))}
              </div>

              <button
                onClick={() => setPage((p) => Math.min(p + 1, totalPages))}
                disabled={page === totalPages}
                style={{
                  ...paginationBtnStyle,
                  opacity: page === totalPages ? 0.5 : 1,
                  cursor: page === totalPages ? "not-allowed" : "pointer",
                }}
              >
                Next ‚Üí
              </button>
            </div>
          )}
        </>
      )}

      {/* CSS Animation for spinner */}
      <style>{`
        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }
      `}</style>
    </div>
  );
}

export default ProductList;
