import { useEffect } from "react";

const AdminAssetsLoader = () => {
  useEffect(() => {
    // Load CSS files
    const cssFiles = [
      "/admin-assets/vendors/mdi/css/materialdesignicons.min.css",
      "/admin-assets/vendors/ti-icons/css/themify-icons.css",
      "/admin-assets/vendors/css/vendor.bundle.base.css",
      "/admin-assets/vendors/font-awesome/css/font-awesome.min.css",
      "/admin-assets/css/style.css"
    ];

    const loadedCss = [];

    cssFiles.forEach((href) => {
      if (!document.querySelector(`link[href="${href}"]`)) {
        const link = document.createElement("link");
        link.rel = "stylesheet";
        link.href = href;
        document.head.appendChild(link);
        loadedCss.push(link);
      }
    });

    // Load JS files (only vendor bundle for Bootstrap functionality)
    const scripts = [
      "/admin-assets/vendors/js/vendor.bundle.base.js"
    ];

    const loadedScripts = [];

    scripts.forEach((src) => {
      if (!document.querySelector(`script[src="${src}"]`)) {
        const script = document.createElement("script");
        script.src = src;
        script.async = false;
        document.body.appendChild(script);
        loadedScripts.push(script);
      }
    });

    // Cleanup function
    return () => {
      loadedCss.forEach((link) => {
        if (link.parentNode) {
          link.parentNode.removeChild(link);
        }
      });
      loadedScripts.forEach((script) => {
        if (script.parentNode) {
          script.parentNode.removeChild(script);
        }
      });
    };
  }, []);

  return null;
};

export default AdminAssetsLoader;
