import { Outlet, useLocation } from "react-router-dom";
import { useEffect } from "react";
import Navbar from "../components/Navbar";
import Sidebar from "../components/Sidebar";
import Footer from "../components/Footer";

const AdminLayout = () => {
  const location = useLocation();

  useEffect(() => {
    // Add admin-specific body class
    document.body.classList.add("admin-layout");

    // Load admin CSS dynamically
    const cssFiles = [
      "/admin-assets/vendors/mdi/css/materialdesignicons.min.css",
      "/admin-assets/vendors/ti-icons/css/themify-icons.css",
      "/admin-assets/vendors/css/vendor.bundle.base.css",
      "/admin-assets/vendors/font-awesome/css/font-awesome.min.css",
      "/admin-assets/css/style.css"
    ];

    cssFiles.forEach((href) => {
      if (!document.querySelector(`link[href="${href}"]`)) {
        const link = document.createElement("link");
        link.rel = "stylesheet";
        link.href = href;
        link.setAttribute("data-admin-css", "true");
        document.head.appendChild(link);
      }
    });

    // Load Bootstrap JS
    const scriptSrc = "/admin-assets/vendors/js/vendor.bundle.base.js";
    if (!document.querySelector(`script[src="${scriptSrc}"]`)) {
      const script = document.createElement("script");
      script.src = scriptSrc;
      script.async = false;
      script.setAttribute("data-admin-js", "true");
      document.body.appendChild(script);
    }

    return () => {
      // Remove admin body classes
      document.body.classList.remove("admin-layout");
      document.body.classList.remove("sidebar-icon-only");

      // Remove all admin CSS when leaving admin
      document.querySelectorAll('link[data-admin-css="true"]').forEach((link) => {
        link.remove();
      });

      // Remove admin JS
      document.querySelectorAll('script[data-admin-js="true"]').forEach((script) => {
        script.remove();
      });
    };
  }, []);

  return (
    <div className="container-scroller">
      <Navbar />

      <div className="container-fluid page-body-wrapper">
        <Sidebar />

        <div className="main-panel">
          <div className="content-wrapper">
            <Outlet />
          </div>

          <Footer />
        </div>
      </div>
    </div>
  );
};

export default AdminLayout;
