import React, { useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import "./Checkout.css";

function Checkout() {
  const navigate = useNavigate();
  const location = useLocation();
  const totalAmount = location.state?.totalAmount || 0;

  const [form, setForm] = useState({
    name: "",
    email: "",
    address: "",
    phone: "",
  });

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  // FINAL Correct HandleSubmit Here
  const handleSubmit = async (e) => {
    e.preventDefault();

    const totalAmount = location.state?.totalAmount;
    if (!totalAmount) return alert("Cart is empty!");

    const orderData = {
      userId: 1,
      ...form,
      totalAmount,
    };

    // Create Order and Store in DB
    const res = await fetch("http://localhost:5000/order/create", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(orderData),
    });

    const data = await res.json();

    if (data.success) {
      // Navigate to Thank You Page
      navigate("/thank-you", { state: { totalAmount } });
    }
  };

  return (
    <div className="checkout-container">
      <h2>Checkout</h2>

      <h3 className="total-box">Total Payable: ₹{totalAmount}</h3>

      <form onSubmit={handleSubmit} className="checkout-form">
        <input
          name="name"
          placeholder="Full Name"
          value={form.name}
          onChange={handleChange}
          required
        />
        <input
          name="email"
          type="email"
          placeholder="Email"
          value={form.email}
          onChange={handleChange}
          required
        />
        <input
          name="phone"
          placeholder="Phone Number"
          value={form.phone}
          onChange={handleChange}
          required
        />
        <textarea
          name="address"
          placeholder="Address"
          value={form.address}
          onChange={handleChange}
          required
        />

        <button type="submit" className="place-order-btn">
          Place Order
        </button>
      </form>
    </div>
  );
}

export default Checkout;
