import React, { useState } from "react";
import { useLocation } from "react-router-dom"; // removed useNavigate
import { loadStripe } from "@stripe/stripe-js";
import "./Checkout.css";

const stripePromise = loadStripe("pk_test_51OmV8ISCq30aaaQjE3So");

function Checkout() {
  const location = useLocation();
  const totalAmount = location.state?.totalAmount || 0;

  const [form, setForm] = useState({
    name: "",
    email: "",
    address: "",
    phone: "",
  });

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();

    const stripe = await stripePromise;

    const session = await fetch("http://localhost:5000/create-checkout-session", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ amount: totalAmount * 100, ...form, user_id: 1 }),
    }).then((res) => res.json());

    const result = await stripe.redirectToCheckout({
      sessionId: session.id,
    });

    if (result.error) alert(result.error.message);
  };

  return (
    <div className="checkout-container">
      <h2>Checkout</h2>
      <h3 className="total-box">Total Payable: ₹{totalAmount}</h3>

      <form onSubmit={handleSubmit} className="checkout-form">
        <input name="name" placeholder="Full Name" onChange={handleChange} required />
        <input name="email" type="email" placeholder="Email" onChange={handleChange} required />
        <input name="phone" placeholder="Phone Number" onChange={handleChange} required />
        <textarea name="address" placeholder="Address" onChange={handleChange} required />

        <button type="submit" className="place-order-btn">
          Pay ₹{totalAmount}
        </button>
      </form>
    </div>
  );
}

export default Checkout;
