import React, { useEffect, useState } from "react";
import "./ThankYou.css";

function ThankYou() {
  const [data, setData] = useState(null);
  const userId = 1; // replace later with logged-in user id

  useEffect(() => {
    async function fetchData() {
      try {
        const res = await fetch(
          `http://localhost:5000/latest-transaction/${userId}`
        );

        const result = await res.json();
        setData(result);
      } catch (err) {
        console.error("Fetch error:", err);
      }
    }

    fetchData();
  }, []);

  if (!data) return <h3>Loading payment details...</h3>;

  return (
    <div className="thankyou-wrapper">
      <div className="thankyou-box">
        <h1>üéâ Thank You!</h1>
        <p>Your payment was successful.</p>

        <div className="order-info">
          <p><b>Transaction ID:</b> {data.transaction.transaction_id}</p>
          <p><b>Status:</b> {data.transaction.status}</p>
          <p><b>Total Paid:</b> ‚Çπ{data.transaction.amount}</p>
          <p><b>Email:</b> {data.transaction.email}</p>
        </div>

        <h3>Purchased Products</h3>
        <ul>
          {data.products.map((p) => (
            <li key={p.product_id}>
              {p.title} √ó {p.quantity} ‚Äî ‚Çπ{p.price}
            </li>
          ))}
        </ul>

        <button
          className="back-home-btn"
          onClick={() => (window.location.href = "/")}
        >
          Continue Shopping
        </button>
      </div>
    </div>
  );
}

export default ThankYou;
