import React, { useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";

function Login() {
  const navigate = useNavigate();

  // Load admin CSS for this standalone page
  useEffect(() => {
    const cssFiles = [
      "/admin-assets/vendors/mdi/css/materialdesignicons.min.css",
      "/admin-assets/vendors/css/vendor.bundle.base.css",
      "/admin-assets/css/style.css"
    ];

    cssFiles.forEach((href) => {
      if (!document.querySelector(`link[href="${href}"]`)) {
        const link = document.createElement("link");
        link.rel = "stylesheet";
        link.href = href;
        link.setAttribute("data-admin-css", "true");
        document.head.appendChild(link);
      }
    });

    return () => {
      document.querySelectorAll('link[data-admin-css="true"]').forEach((link) => {
        link.remove();
      });
    };
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Add your login logic here
    navigate("/admin");
  };

  return (
    <div className="container-scroller">
      <div className="container-fluid page-body-wrapper full-page-wrapper">
        <div className="content-wrapper d-flex align-items-center auth">
          <div className="row flex-grow">
            <div className="col-lg-4 mx-auto">
              <div className="auth-form-light text-left p-5">
                <div className="brand-logo">
                  <img src="/admin-assets/images/logo.svg" alt="logo" />
                </div>

                <h4>Hello! let's get started</h4>
                <h6 className="font-weight-light">Sign in to continue.</h6>

                <form className="pt-3" onSubmit={handleSubmit}>
                  <div className="form-group">
                    <input
                      type="email"
                      className="form-control form-control-lg"
                      placeholder="Username"
                    />
                  </div>

                  <div className="form-group">
                    <input
                      type="password"
                      className="form-control form-control-lg"
                      placeholder="Password"
                    />
                  </div>

                  <div className="mt-3 d-grid gap-2">
                    <button
                      type="submit"
                      className="btn btn-gradient-primary btn-lg font-weight-medium auth-form-btn"
                    >
                      SIGN IN
                    </button>
                  </div>

                  <div className="my-2 d-flex justify-content-between align-items-center">
                    <div className="form-check">
                      <label className="form-check-label text-muted">
                        <input type="checkbox" className="form-check-input" />{" "}
                        Keep me signed in
                      </label>
                    </div>

                    <a href="#forgot" className="auth-link text-primary">
                      Forgot password?
                    </a>
                  </div>

                  <div className="text-center mt-4 font-weight-light">
                    Don't have an account?{" "}
                    <Link to="/admin/register" className="text-primary">
                      Create
                    </Link>
                  </div>
                </form>

              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Login;
