import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

function Cart() {
  const navigate = useNavigate();
  const userId = 1; // Demo user
  const [cartItems, setCartItems] = useState([]);

  // Fetch cart items
  const fetchCart = () => {
    fetch(`http://localhost:5000/cart/${userId}`)
      .then((res) => res.json())
      .then((data) => setCartItems(data))
      .catch(console.error);
  };

  useEffect(() => {
    fetchCart();
  }, []);

  // Remove item from cart
  const handleRemove = (cart_id) => {
    fetch(`http://localhost:5000/cart/item/${cart_id}`, { method: "DELETE" })
      .then((res) => res.json())
      .then(() => fetchCart())
      .catch(console.error);
  };

  // Calculate total price
  const totalPrice = cartItems.reduce(
    (sum, item) => sum + parseFloat(item.price) * item.qty,
    0
  );

  return (
    <div className="cart-container">
      <h2>My Cart</h2>
      {cartItems.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <div>
          {cartItems.map((item) => (
            <div key={item.cart_id} className="cart-item">
              <img
                src={`http://localhost:5000/uploads/${item.image}`}
                alt={item.title}
                className="cart-img"
              />
              <div className="cart-details">
                <h3>{item.title}</h3>
                <p>Qty: {item.qty}</p>
                <p>Price: ₹ {(parseFloat(item.price) * item.qty).toFixed(2)}</p>
                <button onClick={() => handleRemove(item.cart_id)}>Remove</button>
              </div>
            </div>
          ))}

          <div className="cart-total">
            <h3>Total: ₹ {totalPrice.toFixed(2)}</h3>
            <button className="checkout-btn" onClick={() => navigate("/checkout")}>
              Checkout
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Cart;
