import React, { useEffect, useState } from "react";
import { useSearchParams } from "react-router-dom";
import "./ThankYou.css";

function ThankYou() {
  const [searchParams] = useSearchParams();
  const sessionId = searchParams.get("session_id");

  const [payment, setPayment] = useState(null);

  useEffect(() => {
    if (sessionId) {
      fetch(`http://localhost:5000/payment-details/${sessionId}`)
        .then(res => res.json())
        .then(data => setPayment(data))
        .catch(err => console.error(err));
    }
  }, [sessionId]);

  if (!payment) return <h2>Loading payment details...</h2>;

  return (
    <div className="thankyou-wrapper">
      <div className="thankyou-box">
        <h1>ðŸŽ‰ Thank You!</h1>
        <p>Payment Successful</p>

        <p><b>Transaction ID:</b> {payment.transaction_id}</p>
        <p><b>Amount Paid:</b> â‚¹{payment.amount}</p>
        <p><b>Email:</b> {payment.email}</p>
        <p><b>Status:</b> {payment.payment_status}</p>

        <button onClick={() => (window.location.href = "/")}>
          Continue Shopping
        </button>
      </div>
    </div>
  );
}

export default ThankYou;
