import React, { useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import "./ThankYou.css";

function ThankYou() {
  const location = useLocation();
  const navigate = useNavigate();
  const totalAmount = location.state?.totalAmount || 0;

  // üî• Clear Cart Automatically When Page Loads
  useEffect(() => {
    fetch("http://localhost:5000/cart/clear/1", { method: "DELETE" })
      .then(() => console.log("Cart cleared after order"));
  }, []);

  return (
    <div className="thankyou-wrapper">
      <div className="thankyou-box">
        
        <h1>üéâ Order Successful!</h1>
        <p>Thank you for shopping with us.</p>

        <div className="order-info">
          <span>Total Paid</span>
          <b>‚Çπ{totalAmount}</b>
        </div>

        <div className="btn-group">
          <button className="home-btn" onClick={() => navigate("/")}>
            üè† Continue Shopping
          </button>

          <button className="view-order-btn" onClick={() => navigate("/orders")}>
            üì¶ View My Orders
          </button>

          <button className="cart-btn" onClick={() => navigate("/cart")}>
            üõí Go to Cart
          </button>
        </div>

      </div>
    </div>
  );
}

export default ThankYou;
