import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [users, setUsers] = useState([]);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [editId, setEditId] = useState(null);

  // Fetch all users from the server
  useEffect(() => {
    axios.get('http://localhost:5000/users')
      .then(response => setUsers(response.data))
      .catch(error => console.error('Error fetching users:', error));
  }, []);

  // Handle form submit to add or update user
  const handleSubmit = (e) => {
    e.preventDefault();
    if (editId) {
      // Update user
      axios.put(`http://localhost:5000/update/${editId}`, { name, email })
        .then(() => {
          setUsers(users.map(user => user.id === editId ? { id: editId, name, email } : user));
          setEditId(null);
          setName('');
          setEmail('');
        })
        .catch(error => console.error('Error updating user:', error));
    } else {
      // Add new user
      axios.post('http://localhost:5000/add', { name, email })
        .then(response => {
          setUsers([...users, { id: Date.now(), name, email }]);
          setName('');
          setEmail('');
        })
        .catch(error => console.error('Error adding user:', error));
    }
  };

  // Handle delete user
  const handleDelete = (id) => {
    axios.delete(`http://localhost:5000/delete/${id}`)
      .then(() => {
        setUsers(users.filter(user => user.id !== id));
      })
      .catch(error => console.error('Error deleting user:', error));
  };

  // Handle edit user
  const handleEdit = (user) => {
    setName(user.name);
    setEmail(user.email);
    setEditId(user.id);
  };

  return (
    <div>
      <h1>CRUD Operations in React and MySQL</h1>

      <form onSubmit={handleSubmit}>
        <input 
          type="text" 
          value={name} 
          onChange={(e) => setName(e.target.value)} 
          placeholder="Name" 
          required 
        />
        <input 
          type="email" 
          value={email} 
          onChange={(e) => setEmail(e.target.value)} 
          placeholder="Email" 
          required 
        />
        <button type="submit">{editId ? 'Update' : 'Add'}</button>
      </form>

      <ul>
        {users.map(user => (
          <li key={user.id}>
            {user.name} ({user.email}) 
            <button onClick={() => handleEdit(user)}>Edit</button>
            <button onClick={() => handleDelete(user.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default App;
