import React, { useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { loadStripe } from "@stripe/stripe-js";
import { Elements, CardElement, useStripe, useElements } from "@stripe/react-stripe-js";
import "./Checkout.css";

// Your publishable Key ---------------
const stripePromise = loadStripe("pk_test_51OmV8ISCq30aaaQjE3So"); // ðŸ”¥ Replace publish key

function CheckoutForm({ totalAmount }) {
  const navigate = useNavigate();
  const stripe = useStripe();
  const elements = useElements();

  const [form, setForm] = useState({
    name: "",
    email: "",
    phone: "",
    address: "",
  });

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handlePayment = async (e) => {
    e.preventDefault();
    if (!stripe || !elements) return alert("Stripe not loaded");

    // STEP 1 - Create Payment Intent
    const res = await fetch("http://localhost:5000/create-payment-intent", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ amount: totalAmount }),
    });

    const { clientSecret } = await res.json();

    // STEP 2 - Confirm Card Payment
    const paymentResult = await stripe.confirmCardPayment(clientSecret, {
      payment_method: {
        card: elements.getElement(CardElement),
        billing_details: { name: form.name, email: form.email },
      },
    });

    if (paymentResult.error) {
      alert(paymentResult.error.message);
      return;
    }

    // Payment successful
    alert("Payment Successful âœ” Order Creating...");

    // STEP 3 - Store Order + Clear Cart Backend
    await fetch("http://localhost:5000/order/create", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ ...form, totalAmount, user_id: 1 }),
    });

    // Clear Cart
    await fetch(`http://localhost:5000/cart/clear/1`, { method: "DELETE" });

    navigate("/thank-you", { state: { totalAmount } });
  };

  return (
    <form onSubmit={handlePayment} className="checkout-form">
      <input name="name" placeholder="Full Name" value={form.name} onChange={handleChange} required />
      <input name="email" type="email" placeholder="Email" value={form.email} onChange={handleChange} required />
      <input name="phone" placeholder="Phone Number" value={form.phone} onChange={handleChange} required />
      <textarea name="address" placeholder="Address" value={form.address} onChange={handleChange} required />

      <div className="card-box">
        <label>Card Details</label>
        <CardElement className="card-input" />
      </div>

      <button type="submit" className="place-order-btn">Pay â‚¹{totalAmount}</button>
    </form>
  );
}

function Checkout() {
  const location = useLocation();
  const totalAmount = location.state?.totalAmount || 0;

  return (
    <div className="checkout-container">
      <h2>Checkout</h2>
      <h3 className="total-box">Total Payable: â‚¹{totalAmount}</h3>

      <Elements stripe={stripePromise}>
        <CheckoutForm totalAmount={totalAmount} />
      </Elements>
    </div>
  );
}

export default Checkout;
