import React, { useEffect, useState } from "react";
import { useSearchParams } from "react-router-dom";
import "./ThankYou.css";

function ThankYou() {
  const [params] = useSearchParams();
  const transactionId = params.get("transaction_id");

  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!transactionId) return;

    fetch(`http://localhost:5000/transaction/${transactionId}`)
      .then((res) => res.json())
      .then((result) => {
        setData(result);
        setLoading(false);
      })
      .catch(() => setLoading(false));
  }, [transactionId]);

  if (loading) return <h3>Loading payment details...</h3>;

  if (!data) return <h3>Transaction not found</h3>;

  return (
    <div className="thankyou-wrapper">
      <div className="thankyou-box">
        <h1>ðŸŽ‰ Thank You!</h1>
        <p>Your payment was successful.</p>

        <div className="order-info">
          <p><b>Transaction ID:</b> {data.transaction_id}</p>
          <p><b>Status:</b> <span style={{ color: "green" }}>{data.status}</span></p>
          <p><b>Total Paid:</b> â‚¹{data.amount}</p>
          <p><b>Currency:</b> {data.currency.toUpperCase()}</p>
          <p><b>Email:</b> {data.email}</p>
          <p><b>Date:</b> {new Date(data.created_at).toLocaleString()}</p>
        </div>

        <button
          className="back-home-btn"
          onClick={() => (window.location.href = "/")}
        >
          Continue Shopping
        </button>
      </div>
    </div>
  );
}

export default ThankYou;
