import React, { useEffect, useState } from "react";
import "./CartPage.css";
import { useNavigate } from "react-router-dom";

function CartPage() {
  const [items, setItems] = useState([]);
  const userId = 1;
  const navigate = useNavigate();

  const loadCart = async () => {
    const res = await fetch(`http://localhost:5000/cart/${userId}`);
    const data = await res.json();
    setItems(data);
  };

  // Load on first open
  useEffect(() => {
    loadCart();
  }, []);

  // ðŸ”¥ When user comes back after checkout, reload cart
  useEffect(() => {
    const refreshOnReturn = () => loadCart();
    window.addEventListener("focus", refreshOnReturn);

    return () => window.removeEventListener("focus", refreshOnReturn);
  }, []);

  const updateQty = async (cart_id, currentQty, type) => {
    let newQty = type === "inc" ? currentQty + 1 : currentQty - 1;
    if (newQty < 1 || newQty > 5) return;

    await fetch("http://localhost:5000/cart/update", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ cart_id, qty: newQty })
    });

    loadCart();
  };

  const removeItem = async (cart_id) => {
    await fetch(`http://localhost:5000/cart/remove/${cart_id}`, { method: "DELETE" });
    loadCart();
  };

  const totalAmount = items.reduce((sum, item) => sum + item.price * item.qty, 0);

  const handleCheckout = () => navigate("/checkout", { state: { totalAmount } });

  return (
    <div className="cart-container">
      <h2>Your Cart</h2>

      {items.length === 0 ? (
        <p className="empty-cart">ðŸ›’ Your cart is empty.</p>
      ) : (
        <>
          <table className="cart-table">
            <thead>
              <tr>
                <th>Image</th><th>Product</th><th>Qty</th><th>Price</th><th>Total</th><th>Remove</th>
              </tr>
            </thead>

            <tbody>
              {items.map(item => (
                <tr key={item.cart_id}>
                  <td><img src={`http://localhost:5000/uploads/${item.image}`} className="cart-img" /></td>
                  <td>{item.title}</td>
                  <td>
                    <button onClick={() => updateQty(item.cart_id, item.qty, "dec")}>-</button>
                    {item.qty}
                    <button onClick={() => updateQty(item.cart_id, item.qty, "inc")}>+</button>
                  </td>
                  <td>â‚¹{item.price}</td>
                  <td>â‚¹{item.price * item.qty}</td>
                  <td><button onClick={() => removeItem(item.cart_id)}>Remove</button></td>
                </tr>
              ))}
            </tbody>
          </table>

          <div className="checkout-box">
            <h3>Grand Total: â‚¹{totalAmount}</h3>
            <button onClick={handleCheckout} className="checkout-btn">Buy Now</button>
          </div>
        </>
      )}
    </div>
  );
}

export default CartPage;
