import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import "./ThankYou.css";

function ThankYou() {
  const location = useLocation();
  const navigate = useNavigate();
  const [payment, setPayment] = useState(null);
  const [loading, setLoading] = useState(true);

  // get session_id from URL
  const query = new URLSearchParams(location.search);
  const sessionId = query.get("session_id");

  useEffect(() => {
    if (!sessionId) return;

    fetch(`http://localhost:5000/payment-details/${sessionId}`)
      .then((res) => res.json())
      .then((data) => {
        setPayment(data);
        setLoading(false);
      })
      .catch(() => setLoading(false));
  }, [sessionId]);

  if (loading) {
    return <h2 style={{ textAlign: "center" }}>Loading payment details...</h2>;
  }

  return (
    <div className="thankyou-wrapper">
      <div className="thankyou-box">
        <h1>ðŸŽ‰ Thank You!</h1>
        <p>Your payment was successful.</p>

        {payment && (
          <div className="payment-details">
            <div><span>Transaction ID:</span> <strong>{payment.transaction_id}</strong></div>
            <div><span>Status:</span> <strong className="success">{payment.status}</strong></div>
            <div><span>Total Paid:</span> <strong>â‚¹{payment.amount}</strong></div>
            <div><span>Email:</span> <strong>{payment.email}</strong></div>
            <div><span>Name:</span> <strong>{payment.name}</strong></div>
          </div>
        )}

        <button className="continue-btn" onClick={() => navigate("/")}>
          Continue Shopping
        </button>
      </div>
    </div>
  );
}

export default ThankYou;
