import React, { useEffect, useState } from "react";
import { useSearchParams } from "react-router-dom";

function ThankYou() {
  const [params] = useSearchParams();
  const sessionId = params.get("session_id");

  const [data, setData] = useState(null);

  useEffect(() => {
    if (!sessionId) return;

    // 1ï¸âƒ£ get transaction id from Stripe session
    fetch(`http://localhost:5000/payment-details-by-session/${sessionId}`)
      .then(res => res.json())
      .then(result => {
        // 2ï¸âƒ£ fetch DB transaction using transaction_id
        return fetch(`http://localhost:5000/transaction/${result.transaction_id}`);
      })
      .then(res => res.json())
      .then(setData)
      .catch(console.error);

  }, [sessionId]);

  if (!data) return <h3>Loading payment details...</h3>;

  return (
    <div className="thankyou-box">
      <h1>ðŸŽ‰ Thank You!</h1>
      <p>Payment Successful</p>

      <p><b>Transaction ID:</b> {data.transaction_id}</p>
      <p><b>Status:</b> {data.status}</p>
      <p><b>Amount:</b> â‚¹{data.amount}</p>
      <p><b>Email:</b> {data.email}</p>
      <p><b>Date:</b> {new Date(data.created_at).toLocaleString()}</p>
    </div>
  );
}

export default ThankYou;
