import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [users, setUsers] = useState([]);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [editId, setEditId] = useState(null);

  // Fetch all users
  const fetchUsers = () => {
    axios.get('http://localhost:5000/users')
      .then(res => setUsers(res.data))
      .catch(err => console.error('Error fetching users:', err));
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  // Add or update user
  const handleSubmit = (e) => {
    e.preventDefault();

    if (!name || !email) return;

    if (editId) {
      // Update user
      axios.put(`http://localhost:5000/update/${editId}`, { name, email })
        .then(() => {
          fetchUsers(); // reload updated users from backend
          setEditId(null);
          setName('');
          setEmail('');
        })
        .catch(err => console.error('Error updating user:', err));
    } else {
      // Add new user
      axios.post('http://localhost:5000/add', { name, email })
        .then(() => {
          fetchUsers(); // reload all users from backend
          setName('');
          setEmail('');
        })
        .catch(err => console.error('Error adding user:', err));
    }
  };

  // Delete user
  const handleDelete = (id) => {
    axios.delete(`http://localhost:5000/delete/${id}`)
      .then(() => fetchUsers())
      .catch(err => console.error('Error deleting user:', err));
  };

  // Edit user
  const handleEdit = (user) => {
    setName(user.name);
    setEmail(user.email);
    setEditId(user.id);
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>CRUD Operations in React and MySQL</h1>

      <form onSubmit={handleSubmit} style={{ marginBottom: "20px" }}>
        <input 
          type="text" 
          value={name} 
          onChange={(e) => setName(e.target.value)} 
          placeholder="Name" 
          required 
          style={{ marginRight: "10px" }}
        />
        <input 
          type="email" 
          value={email} 
          onChange={(e) => setEmail(e.target.value)} 
          placeholder="Email" 
          required 
          style={{ marginRight: "10px" }}
        />
        <button type="submit">{editId ? 'Update' : 'Add'}</button>
      </form>

      <ul>
        {users.map(user => (
          <li key={user.id} style={{ marginBottom: "10px" }}>
            {user.name} ({user.email}) 
            <button onClick={() => handleEdit(user)} style={{ marginLeft: "10px" }}>Edit</button>
            <button onClick={() => handleDelete(user.id)} style={{ marginLeft: "5px" }}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default App;
