import React, { useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import "./ThankYou.css";

function ThankYou() {
  const location = useLocation();
  const navigate = useNavigate();
  const totalAmount = location.state?.totalAmount || 0;

  // ðŸ†• Clear cart automatically when this page opens
  useEffect(() => {
    fetch("http://localhost:5000/cart/clear/1", { method: "DELETE" })
      .then(() => console.log("Cart cleared after order"))
      .catch(err => console.log("Error:", err));
  }, []);

  return (
    <div className="thankyou-wrapper">
      <div className="thankyou-box">
        <h1>ðŸŽ‰ Thank You!</h1>
        <p>Your order has been placed successfully.</p>

        <div className="order-info">
          <span>Total Paid:</span>
          <strong>â‚¹{totalAmount}</strong>
        </div>

        <button
          className="back-home-btn"
          onClick={() => navigate("/cart")} // ðŸ†• goes to empty cart page
        >
          Go to Cart
        </button>

        <button
          className="back-home-btn"
          onClick={() => navigate("/")} // optional
        >
          Continue Shopping
        </button>
      </div>
    </div>
  );
}

export default ThankYou;
