import "./Home.css";

import React, { useEffect, useState } from "react";
import axios from "axios";

export default function Home() {
  const [bannerData, setBannerData] = useState({});
  const [testimonials, setTestimonials] = useState([]);

  useEffect(() => {
    // Fetch the Home page content (Banner Data)
    axios
      .get("http://localhost:5000/api/page/home")
      .then((res) => {
        setBannerData(res.data.content);  // Assuming content is stored in `content` field in DB
      })
      .catch((err) => console.error("Error fetching banner data:", err));

    // Fetch Testimonials
    axios
      .get("http://localhost:5000/api/testimonials")
      .then((res) => {
        setTestimonials(res.data);  // Assuming testimonial data is returned as an array of objects
      })
      .catch((err) => console.error("Error fetching testimonials:", err));
  }, []);

  return (
    <div className="home">
      {/* FULL BACKGROUND HERO BANNER */}
      <section
        className="hero-section"
        style={{ backgroundImage: "url('/banner.jpg')" }}
      >
        <div className="hero-content">
          {/* Dynamically Render Banner Content */}
          <h1>{bannerData.title || "Welcome To My Website"}</h1>
          <p className="sub-title">{bannerData.subtitles || "WATER Services"}</p>
          <p className="tagline">{bannerData.tagline || "SERVICING YOUR HOME RESTORATION FOR OVER 27 YEARS"}</p>
          <h2 className="phone-number">{bannerData.phone || "619-651-8111"}</h2>
          <p className="desc">{bannerData.description || "No matter what type of damages or restoration services you need, Olympia has you covered!"}</p>
          <button className="emergency-btn">{bannerData.buttonText || "EMERGENCY SERVICE 24/7"}</button>
        </div>
      </section>

      {/* Testimonial Section */}
      <section className="testimonials">
        <h2>What Our Clients Say</h2>
        <div className="testimonial-list">
          {testimonials.map((testimonial) => (
            <div className="testimonial-card" key={testimonial.id}>
              <img src={testimonial.image} alt="Client" />
              <p>{testimonial.message}</p>
              <h4>- {testimonial.name}</h4>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
}
