import React, { useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import "./ThankYou.css";

function ThankYou() {
  const location = useLocation();
  const navigate = useNavigate();
  const totalAmount = location.state?.totalAmount;

  // (Optional) â€” Just safety reload cart if user refreshes page
  useEffect(() => {
    fetch("http://localhost:5000/cart/1")  // user_id = 1
      .then(res => res.json())
      .then(() => console.log("Cart cleared after order."));
  }, []);

  return (
    <div className="thankyou-wrapper">
      <div className="thankyou-box">

        <h1>ðŸŽ‰ Thank You!</h1>
        <p>Your order has been placed successfully.</p>

        <div className="order-info">
          <span>Total Paid:</span>
          <strong>â‚¹{totalAmount}</strong>
        </div>

        {/* Continue Shopping */}
        <button className="back-home-btn" onClick={() => navigate("/")}>
          Continue Shopping
        </button>

        {/* Go to Cart â†’ It will show empty now */}
        <button className="view-cart-btn" onClick={() => navigate("/cart")}>
          View Cart
        </button>

      </div>
    </div>
  );
}

export default ThankYou;
