import { useEffect } from "react";
import { useLocation } from "react-router-dom";

const AdminAssetsLoader = () => {
  const location = useLocation();

  useEffect(() => {
    // Only load admin assets when on admin routes
    const isAdminRoute = location.pathname.startsWith("/admin");
    
    if (!isAdminRoute) return;

    // Load CSS files
    const cssFiles = [
      "/admin-assets/vendors/mdi/css/materialdesignicons.min.css",
      "/admin-assets/vendors/ti-icons/css/themify-icons.css",
      "/admin-assets/vendors/css/vendor.bundle.base.css",
      "/admin-assets/vendors/font-awesome/css/font-awesome.min.css",
      "/admin-assets/css/style.css"
    ];

    const loadedCss = [];

    cssFiles.forEach((href) => {
      if (!document.querySelector(`link[href="${href}"]`)) {
        const link = document.createElement("link");
        link.rel = "stylesheet";
        link.href = href;
        link.setAttribute("data-admin-css", "true");
        document.head.appendChild(link);
        loadedCss.push(link);
      }
    });

    // Load JS files (only vendor bundle for Bootstrap functionality)
    const scripts = [
      "/admin-assets/vendors/js/vendor.bundle.base.js"
    ];

    const loadedScripts = [];

    scripts.forEach((src) => {
      if (!document.querySelector(`script[src="${src}"]`)) {
        const script = document.createElement("script");
        script.src = src;
        script.async = false;
        script.setAttribute("data-admin-js", "true");
        document.body.appendChild(script);
        loadedScripts.push(script);
      }
    });

    // Cleanup function - remove admin assets when leaving admin pages
    return () => {
      // Remove all admin CSS
      document.querySelectorAll('link[data-admin-css="true"]').forEach((link) => {
        link.remove();
      });
      
      // Remove all admin JS
      document.querySelectorAll('script[data-admin-js="true"]').forEach((script) => {
        script.remove();
      });
    };
  }, [location.pathname]);

  return null;
};

export default AdminAssetsLoader;
