import React, { useEffect, useState } from "react";
import axios from "axios";

function Cart() {
  const user_id = 1; // temporary until login
  const [cart, setCart] = useState([]);

  // Load cart from MySQL
  useEffect(() => {
    axios
      .get(`http://localhost:5000/cart/${user_id}`)
      .then((res) => setCart(res.data))
      .catch((err) => console.log(err));
  }, []);

  // Remove cart item
  const removeItem = (cart_id) => {
    axios
      .delete(`http://localhost:5000/cart/remove/${cart_id}`)
      .then(() => {
        setCart(cart.filter((item) => item.cart_id !== cart_id));
      })
      .catch((err) => console.log(err));
  };

  // Calculate total
  const total = cart.reduce(
    (sum, item) => sum + Number(item.price) * item.qty,
    0
  );

  return (
    <div style={{ padding: "20px" }}>
      <h1>My Cart</h1>

      {cart.length === 0 && <p>Your cart is empty.</p>}

      {cart.map((item) => (
        <div
          key={item.cart_id}
          style={{
            border: "1px solid #ddd",
            padding: "15px",
            borderRadius: "6px",
            marginBottom: "15px",
          }}
        >
          <h2>{item.title}</h2>
          <p>Qty: {item.qty}</p>
          <p>Price: ₹ {(Number(item.price) * item.qty).toFixed(2)}</p>

          <button
            onClick={() => removeItem(item.cart_id)}
            style={{
              backgroundColor: "#ff4d4d",
              border: "none",
              padding: "6px 12px",
              color: "white",
              borderRadius: "4px",
              cursor: "pointer",
            }}
          >
            Remove
          </button>
        </div>
      ))}

      {cart.length > 0 && (
        <h2>Total: ₹ {total.toFixed(2)}</h2>
      )}
    </div>
  );
}

export default Cart;
