import React from "react";
import { Link } from "react-router-dom";
import "./ProductCard.css";

function ProductCard({ product }) {
  // Handle image path - check if it's a full URL or needs server prefix
  const getImageUrl = () => {
    if (!product.image) return 'https://via.placeholder.com/300x220?text=No+Image';

    // If already a full URL
    if (product.image.startsWith('http')) {
      return product.image;
    }

    // If starts with /uploads/
    if (product.image.startsWith('/uploads/')) {
      return `http://localhost:5000${product.image}`;
    }

    // If just filename
    if (!product.image.includes('/')) {
      return `http://localhost:5000/uploads/${product.image}`;
    }

    // Default: add server prefix
    return `http://localhost:5000/${product.image}`;
  };

  return (
    <div className="product-card">
      <img
        src={getImageUrl()}
        alt={product.title}
        className="product-img"
        onError={(e) => {
          e.target.src = 'https://via.placeholder.com/300x220?text=No+Image';
        }}
      />
      <h3>{product.title}</h3>
      <p>â‚¹ {(parseFloat(product.price) || 0).toFixed(2)}</p>

      <div className="product-actions">
        <Link to={`/product/${product.id}`} className="btn details-btn">
          View Details
        </Link>
      </div>
    </div>
  );
}

export default ProductCard;
